mapper.select:
SELECT m.id,m.name,m.url,m.parent_id,a.name AS action_name, a.url AS action_url,a.menu_id AS action_menu_id,a.id AS action_id,a.method FROM sys_menu m LEFT JOIN sys_action a on m.id = a.menu_id
AND m.parent_id = #{parentId}
ORDER BY m.order_num,CONVERT(a.name USING gbk) COLLATE gbk_chinese_ci

SELECT id, name, url, parent_id, order_num FROM sys_menu
AND parent_id = #{parentId}
ORDER BY order_num

SELECT id, name, url, parent_id, order_num
FROM sys_menu
WHERE id = #{id}
